const path = require("path")

module.exports = function(props, config) {
  return {
    name: props.name,
    extension: "js",
    dir: props.path,
    role: "main",
    template: {
      name: props.type === "es6class"
        ? "ES6ClassComponent.js"
        : "StatelessFunctionalComponent.js",
      dir: path.resolve(__dirname, "templates")
    }
  }
}
